import React, { useEffect } from 'react';
import { Row, Col, ListGroup, Image, Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../components/Message';
import Loader from '../components/Loader';
import { getOrderDetails, deliverOrder } from '../actions/orderActions';
import { ORDER_DELIVER_RESET } from '../constants/orderConstants';
import KhaltiCheckout from 'khalti-checkout-web';

const OrderScreen = ({ match, history }) => {
	const orderId = match.params.id;

	const dispatch = useDispatch();

	const orderDetails = useSelector((state) => state.orderDetails);
	const { order, loading, error } = orderDetails;

	const userLogin = useSelector((state) => state.userLogin);
	const { userInfo } = userLogin;

	const orderDeliver = useSelector((state) => state.orderDeliver);
	const { loading: loadingDeliver, success: successDeliver } = orderDeliver;

	if (!loading) {
		//Price calculation
		const addDecimals = (num) => {
			return (Math.round(num * 100) / 100).toFixed(2);
		};

		order.itemsPrice = addDecimals(
			order.orderItems.reduce((acc, item) => acc + item.price * item.qty, 0)
		);
	}

	useEffect(() => {
		if (!userInfo) {
			history.push('/login');
		}

		if (!order || successDeliver) {
			dispatch({ type: ORDER_DELIVER_RESET });
			dispatch(getOrderDetails(orderId));
		} else {
			let config = {
				// replace this key with yours
				publicKey: 'test_public_key_3fdfd9a5bc0344ff9a4087c020d9cafe',
				productIdentity: '1234567890',
				productName: 'Drogon',
				productUrl: 'http://gameofthrones.com/buy/Dragons',
				eventHandler: {
					onSuccess(payload) {
						// hit merchant api for initiating verfication
						console.log(payload);
					},
					// onError handler is optional
					onError(error) {
						// handle errors
						console.log(error);
					},
					onClose() {
						console.log('widget is closing');
					},
				},
				paymentPreference: [
					'KHALTI',
					'EBANKING',
					'MOBILE_BANKING',
					'CONNECT_IPS',
					'SCT',
				],
			};

			let checkout = new KhaltiCheckout(config);
			let btnKhalti = document.getElementById('btnKhalti');
			const Khalti = async () => {
				// minimum transaction amount must be 10, i.e 1000 in paisa.
				checkout.show({ amount: cart.totalPrice * 100 });
			};
		}
	}, [dispatch, history, order, orderId, successDeliver, userInfo]);

	// useEffect(() => {
	//   dispatch(getOrderDetails(orderId))
	// }, [dispatch, orderId])

	const deliverHandler = () => {
		dispatch(deliverOrder(order));
	};

	return loading ? (
		<Loader />
	) : error ? (
		<Message variant="danger">{error}</Message>
	) : (
		<>
			<h1>Order {order._id}</h1>
			<Row>
				<Col md={8}>
					<ListGroup variant="flush">
						<ListGroup.Item>
							<h2> Shipping </h2>
							<p>
								<strong> Name: </strong> {order.user.name}{' '}
							</p>
							<p>
								<strong> E-mail: </strong>{' '}
								<a href={`mailto:${order.user.email}`}> {order.user.email}</a>
							</p>
							<p>
								<strong> Address: </strong>
								{order.shippingAddress.address}, {order.shippingAddress.city},{' '}
								{order.shippingAddress.postalCode},{' '}
								{order.shippingAddress.country}
							</p>
							{order.isDelivered ? (
								<Message variant="success">
									{' '}
									Delivered on {order.deliveredAt}
								</Message>
							) : (
								<Message variant="danger"> Not Delivered!</Message>
							)}
						</ListGroup.Item>

						<ListGroup.Item>
							<h2>Payment Method</h2>
							<p>
								<strong> Method: </strong>
								{order.paymentMethod}
							</p>
							{order.isPaid ? (
								<Message variant="success"> Paid on {order.paidAt}</Message>
							) : (
								<Message variant="danger"> Not Paid!</Message>
							)}
						</ListGroup.Item>

						<ListGroup.Item>
							<h2>Order Items</h2>
							{order.orderItems.length === 0 ? (
								<Message>Order is empty! </Message>
							) : (
								<ListGroup variant="flush">
									{order.orderItems.map((item, index) => (
										<ListGroup.Item key={index}>
											<Row>
												<Col md={1}>
													<Image
														src={item.image}
														alt={item.name}
														fluid
														rounded
													/>
												</Col>
												<Col>
													<Link to={`/product/${item.product}`}>
														{item.name}
													</Link>
												</Col>
												<Col md={4}>
													{item.qty} x Rs. {item.price} = Rs.
													{item.qty * item.price}
												</Col>
											</Row>
										</ListGroup.Item>
									))}
								</ListGroup>
							)}
						</ListGroup.Item>
					</ListGroup>
				</Col>
				<Col md={4}>
					<Card>
						<ListGroup variant="flush">
							<ListGroup.Item>
								<h2> Order Summary</h2>
							</ListGroup.Item>

							<ListGroup.Item>
								<Row>
									<Col> Items </Col>
									<Col> Rs.{order.itemsPrice}</Col>
								</Row>
							</ListGroup.Item>

							<ListGroup.Item>
								<Row>
									<Col> Shipping </Col>
									<Col> Rs.{order.shippingPrice}</Col>
								</Row>
							</ListGroup.Item>

							<ListGroup.Item>
								<Row>
									<Col> Tax </Col>
									<Col> Rs.{order.taxPrice}</Col>
								</Row>
							</ListGroup.Item>

							<ListGroup.Item>
								<Row>
									<Col> Total </Col>
									<Col> Rs.{order.totalPrice}</Col>
								</Row>
							</ListGroup.Item>
							<ListGroup.Item>
								<Button type="button" className="btn-block" onClick={Khalti}>
									pay with khalti
								</Button>
							</ListGroup.Item>

							{loadingDeliver && <Loader />}
							{userInfo && userInfo.isAdmin && !order.isDelivered && (
								<ListGroup.Item>
									<Button
										type="button"
										className="btn btn-block"
										onClick={deliverHandler}
									>
										MARK AS DELIVERED
									</Button>
								</ListGroup.Item>
							)}
						</ListGroup>
					</Card>
				</Col>
			</Row>
		</>
	);
};

export default OrderScreen;
