import React, { useEffect } from 'react';
import { LinkContainer } from 'react-router-bootstrap';
import { Table, Button, Row, Col } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../components/Message';
import Loader from '../components/Loader';
import { listBlogs, deleteBlogs } from '../actions/blogActions';

const BlogListScreen = ({ history, match }) => {
	const dispatch = useDispatch();

	// const blogList = useSelector((state) => state.blogList);
	// const { loading, error, users } = blogList;

	const userLogin = useSelector((state) => state.userLogin);
	const { userInfo } = userLogin;

	const blogDelete = useSelector((state) => state.blogDelete);
	const { success: successDelete } = blogDelete;

	useEffect(() => {
		if (userInfo && userInfo.isAdmin) {
			dispatch(listBlogs());
		} else {
			history.push('/login');
		}
	}, [dispatch, history, userInfo]);

	const deleteHandler = (id) => {
		if (window.confirm('Do you want to delete the blog?')) {
			dispatch(deleteBlogs(id));
		}
	};

	return (
		<>
			<h1 style={{ textAlign: 'left' }}> Blogs</h1>
			<Table striped bordered hover responsive className="table-sm">
				<thead>
					<tr>
						<th> ID </th>
						<th> Blog Name </th>
						<th> Image </th>
						<th> Text </th>
						<th> </th>
					</tr>
				</thead>
				<tbody>
					{blogs.map((blog) => (
						<tr key={blog._id}>
							<td>{blog._id}</td>
							<td>{blog.name}</td>
							<td>
								<a href={`mailto:${user.email}`}>{user.email}</a>
							</td>
							<td>
								{user.isAdmin ? (
									<i className="fas fa-check" style={{ color: 'green' }}>
										{' '}
									</i>
								) : (
									<i className="fas fa-times" style={{ color: 'red' }}></i>
								)}
							</td>
							<td>
								<LinkContainer to={`/admin/user/${user._id}/edit`}>
									<Button variant="light" className="btn-sm">
										<i className="fas fa-edit"></i>
									</Button>
								</LinkContainer>
								<Button
									variant="danger"
									className="btn-sm"
									onClick={() => deleteHandler(user._id)}
								>
									<i className="fas fa-trash"></i>
								</Button>
							</td>
						</tr>
					))}
				</tbody>
			</Table>
		</>
	);
};

export default BlogListScreen;
