import React from 'react';
import { Route } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import ProductSearch from './ProductSearch';
import { logout } from '../actions/userActions';

const Header = () => {
	const dispatch = useDispatch();

	const userLogin = useSelector((state) => state.userLogin);
	const { userInfo } = userLogin;

	const logoutHandler = () => {
		dispatch(logout());
	};

	return (
		<header>
			<div className="top-nav" expand="lg">
				<a href="tel:(222) 400-630">(222) 400-630</a>
				<a href="mailto: info@domain.com">info@domain.com</a>
			</div>
			<Navbar className="nav" expand="lg" collapseOnSelect>
				<Container>
					<LinkContainer to="/">
						<Navbar.Brand>Poshak</Navbar.Brand>
					</LinkContainer>
					<Navbar.Toggle aria-controls="basic-navbar-nav" />
					<Navbar.Collapse id="basic-navbar-nav">
						<Route
							render={({ history }) => <ProductSearch history={history} />}
						/>
						<Nav className="ml-auto">
							<LinkContainer to="/blogs">
								<Nav.Link> Blogs</Nav.Link>
							</LinkContainer>

							{/* <LinkContainer to='/lookbook'>
                <Nav.Link> Look Book</Nav.Link>
              </LinkContainer> */}

							<LinkContainer to="/cart">
								<Nav.Link>
									<i className="fas fa-shopping-cart"></i> Cart
								</Nav.Link>
							</LinkContainer>
							{userInfo ? (
								<NavDropdown title={userInfo.name} id="username">
									<LinkContainer to="/profile">
										<NavDropdown.Item> Profile </NavDropdown.Item>
									</LinkContainer>
									<NavDropdown.Item onClick={logoutHandler}>
										{' '}
										Logout{' '}
									</NavDropdown.Item>
								</NavDropdown>
							) : (
								<LinkContainer to="/login">
									<Nav.Link>
										<i className="fas fa-user"></i>Sign In{' '}
									</Nav.Link>
								</LinkContainer>
							)}
							{userInfo && userInfo.isAdmin && (
								<NavDropdown title="Admin" id="adminmenu">
									<LinkContainer to="/admin/userList">
										<NavDropdown.Item> Users </NavDropdown.Item>
									</LinkContainer>

									<LinkContainer to="/admin/productList">
										<NavDropdown.Item> Products </NavDropdown.Item>
									</LinkContainer>

									<LinkContainer to="/admin/orderList">
										<NavDropdown.Item> Orders </NavDropdown.Item>
									</LinkContainer>
								</NavDropdown>
							)}
						</Nav>
					</Navbar.Collapse>
				</Container>
			</Navbar>
		</header>
	);
};

export default Header;

